var React = require('react');

var loginReact = React.createClass({
 
	render: function(){

		return(
			<div> 
				<h1> LOGIN PAGE </h1>
				<form id='login-form' method='post' onSubmit={this.onSubmit}>
					Email, 
					<br/>
					<input id='email-field' type="text" name="email" />
				  	<br/>
					Password:
					<br/>
				  	<input id='password-field' type="password" name="password" />
				  	<br/>
				  	<input type="submit" value="Submit"/>
				</form>
				<br/>
				<h2 id='status'> Please login with your email and password </h2> 
			</div>
			)
	}
});

module.exports=loginReact;